mapper.select:
SELECT project_id
FROM project_member
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM project_member
WHERE project_id = #{projectId}
  AND user_id = #{userId}

mapper.select:
SELECT id, project_id, user_id, role, joined_time
FROM project_member
WHERE project_id = #{projectId}
  AND user_id = #{userId}

SELECT id, project_id, user_id, role, joined_time
FROM project_member
WHERE project_id = #{projectId}
